package operators;

public class BitwiseOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=4;
int b=5;
int c=a^b;
int d=a&b;
int e=a|b;
int f=~a;
int g=~b;
System.out.println("eor-"+c);
System.out.println("and-"+d);
System.out.println("or-"+e);
System.out.println("negation-"+f);
System.out.println("negation-"+g);
	}

}
